namespace T08.BigNumberSum
{
    using System;
    using System.Text;

    /// <summary>
    /// 8. Write a method that adds two positive integer numbers represented as arrays of digits 
    ///    (each array element arr[i] contains a digit; the last digit is kept in arr[0]). 
    ///    Each of the numbers that will be added could have up to 10 000 digits.
    /// </summary>
    public class BigNumberSum
    {
        public static string SumNumbers(string first, string second)
        {
            if (first[0] == '-' || second[0] == '-' || first.Contains(".") || first.Contains(",") || second.Contains(".") || second.Contains(","))
            {
                throw new ArgumentException("This method can't sum negative numbers. Use it only for positive integer numbers!");
            }

            // Find bigger array length
            int biggerLength = Math.Max(first.Length, second.Length);

            // Add one more for carry
            biggerLength++;

            int[] firstNum = ConvertToArrayOfInts(first, biggerLength);
            int[] secondNum = ConvertToArrayOfInts(second, biggerLength);

            int[] tempResult = new int[biggerLength];

            int carry = 0;

            for (int i = 0; i < biggerLength; i++)
            {
                int temp = firstNum[i] + secondNum[i] + carry;

                tempResult[i] = temp % 10;

                if (temp > 9)
                {
                    carry = 1;
                }
                else
                {
                    carry = 0;
                }
            }

            // Convert temp result to string remove last 0 if necessary
            int loopIterations = tempResult.Length - 1;
            if (tempResult[tempResult.Length - 1] == 0)
            {
                loopIterations--;
            }

            StringBuilder result = new StringBuilder();

            for (int i = loopIterations; i >= 0; i--)
            {
                result.Append(tempResult[i]);
            }

            return result.ToString();
        }

        private static void Main()
        {
            // I think you can test with any number you want, below are 20000 digits + 10000 digits
            // string first = "";
            // string second = "";
            string first = Console.ReadLine();
            string second = Console.ReadLine();

            Console.WriteLine("Result is: {0}", SumNumbers(first, second));
        }

        private static int[] ConvertToArrayOfInts(string input, int length)
        {
            int[] result = new int[length];

            char[] tempResult = input.ToCharArray();
            Array.Reverse(tempResult);

            for (int i = 0; i < tempResult.Length; i++)
            {
                result[i] = int.Parse(tempResult[i].ToString());
            }

            return result;
        }
    }
}
